INSERT INTO vorvertragTemplates (tenantId, version, isActive, templateText, checkboxZuarbeitText, checkboxTeilnahmeText, checkboxDatenschutzText, checkboxAgbText)
SELECT 
  id as tenantId,
  '1.0' as version,
  true as isActive,
  'Vorvertrag: KOMPASS-Qualifizierung

Teilnehmer: {{vorname}} {{nachname}}
Adresse: {{adresse}}
E-Mail: {{email}}
Telefon: {{telefon}}

Gewählter Kurs: {{kurstitel}}
Starttermin: {{starttermin}}
Kurspreis: €{{kurspreis}} (brutto)
Förderung: €{{foerderbetrag}} ({{foerderprozent}}%)
Eigenanteil: €{{eigenanteil}}

━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
Ihre Bestätigungen:
━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━

✅ Fristgerechte Zuarbeit
{{checkboxZuarbeitText}}

✅ Verbindliche Teilnahme
{{checkboxTeilnahmeText}}

✅ Datenschutzerklärung
{{checkboxDatenschutzText}}

✅ AGB & Widerrufsrecht
{{checkboxAgbText}}

Unterschrieben am: {{signedAt}}
IP-Adresse: {{ipAddress}}' as templateText,
  'Ich verpflichte mich, alle angeforderten Dokumente innerhalb von 7 Kalendertagen nach dem Erstberatungsgespräch einzureichen. Bei Verzögerung: €499 Gebühr (exkl. USt.).' as checkboxZuarbeitText,
  'Ich nehme verbindlich am Starttermin teil. Bei Verzögerung (nicht von mir verschuldet): Teilnahme am nächstmöglichen Folgetermin.' as checkboxTeilnahmeText,
  'Ich willige in die Datenverarbeitung durch Sachs Consulting Ltd. und Entscheiderakademie GmbH ein.' as checkboxDatenschutzText,
  'Ich habe die AGB und Widerrufsbelehrung zur Kenntnis genommen und akzeptiere diese.' as checkboxAgbText
FROM tenants
WHERE NOT EXISTS (
  SELECT 1 FROM vorvertragTemplates WHERE vorvertragTemplates.tenantId = tenants.id
);

